sys_script_include.script:
var Find_NeedIt_Tasks_Due_Soon = Class.create();
Find_NeedIt_Tasks_Due_Soon.prototype = {
    initialize: function() {
		var test = new GlideDateTime().GetDate();
		var test2 = new GlideDateTime().getDate().addDaysUTC(1).addSeconds(-1);

		var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');
		overdueNITasks.addQuery('due_date', '>=', new GlideDateTime().GetDate());
		overdueNITasks.addQuery('due_date', '<=', new GlideDateTime().getDate().addDaysUTC(1).addSeconds(-1));
		overdueNITasks.addQuery('state', '!=', 3);

		overdueNITasks.query();
		// Write a log message for each overdue NeedIt Task Record
		while (overdueNITasks.next()) {
			gs.info("Due Soon NeedIt Task record = " + overdueNITasks.number);
		}
    },

    type: 'Find_NeedIt_Tasks_Due_Soon'
};
